#pragma config(Motor,  mtr_S1_C1_2,     motor2,        tmotorTetrix, openLoop)
#pragma config(Motor,  mtr_S1_C2_1,     frontLeft,        tmotorTetrix, openLoop, reversed)
#pragma config(Motor,  mtr_S1_C2_2,     frontRight,        tmotorTetrix, openLoop)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

#include "JoystickDriver.c"

void motorControl(int leftPower, int rightPower, int pivotPower) {
	int threshold = 10;
	//int leftPower = 0, rightPower = 0, pivotPower = 0;
	int power = joystick.joy1_y1;
	pivotPower = joystick.joy1_x2;
	if(pivotPower > threshold || pivotPower < -threshold) {

		if(pivotPower > threshold) {
			rightPower = pivotPower;
			leftPower = -pivotPower;
		} else if(pivotPower < -threshold) {
			leftPower = pivotPower;
			rightPower = -pivotPower;
		}
	}
	motor[pivotMotor] = pivotPower;
	if(power > threshold) {
		if(leftPower < 0) {
			leftPower -= power;
			rightPower += power;
		} else if(leftPower > 0) {
			leftPower += power;
			rightPower -= power;
		}
	} else if(power < -threshold) {
		if(leftPower < 0) {
			leftPower += power;
			rightPower -= power;
		} else if(leftPower > 0) {
			leftPower -= power;
			rightPower += power;
		}
	}
	motor[frontRight] = rightPower;
	motor[frontLeft] = leftPower;
}

task main()
{
	int leftPower = 0, rightPower = 0, pivotPower = 0;
	while(true) {
		//motorControl(leftPower, rightPower, pivotPower);
		motor[frontRight] = 100;
		wait1Msec(1000);

		motor[frontRight] = 0;
	}
}
